  c6 =  (1./2. * sqrt(YDATA_B_ee[i] / YDATA_B_mumu[i]) * errYDATA_B_emu[i]) * (1./2. * sqrt(YDATA_B_ee[i] / YDATA_B_mumu[i]) * errYDATA_B_emu[i]) ;
  errNB = sqrt(c4 + c5 + c6); 
  YDATA_Extrapolated_A_mumu[i]     = NB * YDATA_R_corrected_N_mumu[6]; 
  errYDATA_Extrapolated_A_mumu[i]  = sqrt ((NB * errYDATA_R_corrected_N_mumu[6] * NB * errYDATA_R_corrected_N_mumu[6]) + YDATA_R_corrected_N_mumu[6] * YDATA_R_corrected_N_mumu[6] * (c4 + c5 + c6));

  
  
  YTotalDataCorrected_A_mumu[i]  = Yall_A_mumu[i] - YDY_A_mumu[i] + YDATA_Extrapolated_A_mumu[i];
  errYTotalDataCorrected_A_mumu[i] = sqrt(errYall_A_mumu[i]*errYall_A_mumu[i] + errYDY_A_mumu[i]*errYDY_A_mumu[i] + errYDATA_Extrapolated_A_mumu[i]*errYDATA_Extrapolated_A_mumu[i]);
